import './App.css';
import { useEffect } from "react";
import alphabets from './alphabets.js';
function App() {
  use
  function sound(alphabet) {
    const audio = document.getElementById(alphabet);
    console.log(audio);
    audio.play();
  }
  return (
    <div className="App">
      <div id="drum-machine">
        <div id="display">

        </div>
        <div className="drum-pads">
          {alphabets.map((alphabets)=>( 
          <div
           onClick={()=>{
            sound(alphabets.text)
           }} 
           className="drum-pad" 
           id={alphabets.src}
          >
            {alphabets.text}
            <audio 
              className="clip" 
              id={alphabets.text} 
              src={alphabets.src}
            ></audio>
          </div>))}
        </div>

      </div>
    </div>
  );
}

export default App;
